import React from "react";
import { Placemark } from "react-yandex-maps";
function Point(props) {
  return (
    <div>
      <Placemark
        key={props.id}
        geometry={props.coor}
        options={{
          iconColor: "#ff0000", // Цвет маркера
          iconLayout: "default#imageWithContent",
          iconImageHref: "path/to/your/icon.png", // URL изображения маркера
          iconContentLayout:
            '<div style="color: #000000; font-weight: bold;">Ваш текст</div>',
          iconContentOffset: [0, -30], // Смещение для размещения подписи под маркером
        }}
      />
    </div>
  );
}

export default Point;
